import React,{useState} from 'react'
import axios from 'axios';
function Emicalcdashboard() {
    const [enrollerData,setEnrollerData]=useState({name:'',address:'',pincode:'',mobile:'',email:'',adhar_card:'',pan_card:'',loan_amount:'',intrest_rate:'',tenure:''});
    const [loan_amount,setLoanamt]=useState('');
    const [tenure,setTenureamt]=useState('');
    const [intrest_rate,setIntrestamt]=useState('');
    const [emiDetails, setEmiDetail]=useState(null);

    const calculateemi=async ()=>{
        //api\
       

       
        const responses=await axios.post(`http://localhost:8001/api/emi_info/`,{loan_amount:loan_amount,tenure:tenure,intrest_rate:intrest_rate});
        console.log(responses.data);
        setEmiDetail(responses.data);


    }

  return (
    <div>
        <h1>EMI CALCULATOR DASHBOARD</h1>
        <div class="accept_inout">
            <input type="number" placeholder='entre the loan amt ' value={loan_amount}  onChange={(e)=>setLoanamt(e.target.value)}/>
            <input type="number" placeholder='entre the tenure ' value={tenure}  onChange={(e)=>setTenureamt(e.target.value)}/>
            <input type="number" placeholder='entre the interst ' value={intrest_rate}  onChange={(e)=setIntrestamt(e.target.value)}/>
            <button onClick={calculateemi}>Submit Details</button>
            {/* if only emidetail ther e */}
            {emiDetails && (
                <div>
                    <h2> emi detail</h2>
                    <p>EMI val : <span>{emiDetails.emi}</span></p>
                    <p> Principal val:<span>{emiDetails.principal_amt}</span></p>
                    <p> Total Amount : <span> {emiDetails.total_amt}</span></p>
                    <p>Interest Amount: <span> {emiDetails.interest_amt}</span></p>
                </div>
            )}
            //popup wala div
           <div>
            {/* <input type="text" placeholder="entere the name " value={} onChange={}/>
            <input type="text" placeholder="entere the address " value={} onChange={}/>
            <input type="text" placeholder="entere the pincode " value={} onChange={}/>
            <input type="text" placeholder="entere the mobile " value={} onChange={}/>
            <input type="text" placeholder="entere the email " value={} onChange={}/>
          */}


           </div>

        </div>
      
    </div>
  )
}

export default Emicalcdashboard
